import React, { useState } from "react";
import { MDBRow, MDBCol, MDBBtn, MDBCard } from "mdbreact";
//import { ToastContainer, toast } from "react-toastify";
//import "react-toastify/dist/ReactToastify.css";
//import axios from "axios";

function ShippingForm() {
  const [name, setName] = useState("");
  const [weight, setWeight] = useState(1);
  const [color, setColor] = useState("#3F51B5");
  const [country, setCountry] = useState("11.42");

  const cardStyle = {
   width: "500px",
    left: "50%",
    top: "50%",
     position: "absolute",
     transform: "translate(-50%, -50%)",
    padding: "10px",
   };

   const submitHandler =  (event) => {
    event.preventDefault();

     if (name === "" || weight === "" || color === "" || country === "") {
     alert("Please fill all the fields");
     return;
     }

     event.target.className="was-validated";
    
     // {
    //     position: "top-right",
    //     autoClose: 5000,
    //     hideProgressBar: false,
    //     closeOnClick: true,
    //     pauseOnHover: true,
    //     draggable: true,
    //     progress: undefined,
    //   });
    //   return;
    

  //   event.target.className += " was-validated";

   const multipliers = {
     Sweden: 11.42,
     China: 8.71,
      Brazil: 7.43,
     Australia: 1.83,
     };
    };

  //   const cost = (multipliers[country] * weight).toFixed(2);

  //   const items = {
  //     name: name,
  //     weight: weight,
  //     color: color,
  //     country: country,
  //     cost: cost,
  //   };

  //   await axios
  //     .post("http://localhost:5000/create-order", items)
  //     .then((res) => {
  //       toast.success("Order placed", {
  //         position: "top-right",
  //         autoClose: 5000,
  //         hideProgressBar: false,
  //         closeOnClick: true,
  //         pauseOnHover: true,
  //         draggable: true,
  //         progress: undefined,
  //       });
  //     })
  //     .catch((err) => {
  //       toast.error("Error while creating order", {
  //         position: "top-right",
  //         autoClose: 5000,
  //         hideProgressBar: false,
  //         closeOnClick: true,
  //         pauseOnHover: true,
  //         draggable: true,
  //         progress: undefined,
  //       });
  //     });
  // };
  return (
    <div>
      <MDBCard style={cardStyle}>
        <form className="needs-validation" noValidate onSubmit={submitHandler}>
          <MDBRow>
            <MDBCol md="12" className="mb-3">
              <label htmlFor="name">Name</label>
              <input
                type="text"
                className="form-control"
                onChange={(event) => setName(event.target.value)}
                required
                id="name"
                value={name}
              />
            </MDBCol>
            <MDBCol md="12" className="mb-3">
              <label>Weight(kg)</label>
              <input
                type="number"
                className="form-control"
                onChange={(event) => setWeight(event.target.value)}
                required
                value={weight}
                min="0"
              />
            </MDBCol>
            <MDBCol md="12" className="mb-3">
              <label>Box Color</label>
              <input
                type="color"
                className="form-control"
                onChange={(event) => setColor(event.target.value)}
                required
                value={color}
              />
            </MDBCol>
            <MDBCol md="12" className="mb-3">
              <label>Destination Country</label>
              <select
                className="browser-default custom-select"
                onChange={(event) => setCountry(event.target.value)}
                required
                value={country}
              >
                <option value="Sweden" default>
                  Sweden
                </option>
                <option value="China">China</option>
                <option value="Brazil">Brazil</option>
                <option value="Australia">Australia</option>
              </select>
            </MDBCol>
            <MDBCol md="12" className="mb-3">
              <MDBBtn color="indigo" type="submit">
                Save
              </MDBBtn>
            </MDBCol>
          </MDBRow>
        </form>
      </MDBCard>
    </div>
  );
}
export default ShippingForm;
